INSERT INTO `tb_article` VALUES ('3239', '247', '美剧推荐，值得一看', '<p>最新看一些美剧~</p><p>《迷失lost》 飞机失事后的奇遇~太奇遇了~~~~</p><p>《致命毒师breaking bad》 老人制毒的故事，人性的释放~</p><p>《黑色孤儿orphanblack》 克隆人clones的故事</p><p>《冰与火之歌》 号称神剧~！~</p><p>《黑镜black mirror》 这个剧特别推荐，每集都是独立的，但是意义深刻。</p><p>以上看过的我都认为是超级推荐</p><p><br/></p><p>《灭世<span style=\"font-family: arial, 宋体, sans-serif; line-height: 24px; text-indent: 32px;\">Revolution</span>》 &nbsp;只看了一季</p><p>《越狱<em style=\"font-family: &#39;Microsoft Yahei&#39;; line-height: 24px; text-indent: 32px; white-space: normal;\">Prison Break</em>》 只看了一季</p><p>《罪恶黑名单<span style=\"font-family: arial, 宋体, sans-serif; line-height: 26px;\">The Blacklist》</span> &nbsp;<span style=\"line-height: 22.8571434020996px;\">只看了一季</span></p><p><br/></p><p>之后打算看</p><p>黑道家族和广告狂人</p><p><br/></p><p>吸血鬼日记</p><p>行尸走肉</p><p><span style=\"line-height: 1.42857143;\">汉尼拔&nbsp;</span></p><p><br/></p>', '287', '0', '11', '1', '1', '2', '10', null, null, null, null, null, '10', '2015-04-11', '系统管理员', null, null, '2015-04-11 21:49:36', '2015-04-11 21:49:36', '1');
