INSERT INTO `tb_article` VALUES ('3262', '247', '水果沙拉', '<p><em class=\"step\" style=\"border: 0px; outline: 0px; font-size: 50px; vertical-align: baseline; color: rgb(255, 50, 50); text-align: center; font-family: arial; height: 50px; width: 100px; display: block; float: left; line-height: 50px; background: transparent;\">1.</em></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-right: 20px; padding-bottom: 10px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; position: relative; background: transparent;\">准备所需材料。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-right: 20px; padding-bottom: 10px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; position: relative; background: transparent;\"><img class=\"conimg\" src=\"http://site.meishij.net/rs/237/178/1232237/n1232237_142459118511334.jpg\" alt=\"\" style=\"margin-right: auto; margin-left: auto; outline: 0px; vertical-align: baseline; display: block; max-width: 550px; background: transparent;\"/></p><p><em class=\"step\" style=\"border: 0px; outline: 0px; font-size: 50px; vertical-align: baseline; color: rgb(255, 50, 50); text-align: center; font-family: arial; height: 50px; width: 100px; display: block; float: left; line-height: 50px; background: transparent;\">2.</em></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-right: 20px; padding-bottom: 10px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; position: relative; background: transparent;\">将苹果洗净去皮，切成小丁用淡盐水泡5分钟，弥猴桃去皮切成小丁、圣女果同样也是洗净切小丁。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-right: 20px; padding-bottom: 10px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; position: relative; background: transparent;\"><img class=\"conimg\" src=\"http://site.meishij.net/rs/237/178/1232237/n1232237_142459120362767.jpg\" alt=\"\" style=\"margin-right: auto; margin-left: auto; outline: 0px; vertical-align: baseline; display: block; max-width: 550px; background: transparent;\"/></p><p><em class=\"step\" style=\"border: 0px; outline: 0px; font-size: 50px; vertical-align: baseline; color: rgb(255, 50, 50); text-align: center; font-family: arial; height: 50px; width: 100px; display: block; float: left; line-height: 50px; background: transparent;\">3.</em></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-right: 20px; padding-bottom: 10px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; position: relative; background: transparent;\">将泡好的苹果捞起沥干水份。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-right: 20px; padding-bottom: 10px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; position: relative; background: transparent;\"><img class=\"conimg\" src=\"http://site.meishij.net/rs/237/178/1232237/n1232237_142459123520586.jpg\" alt=\"\" style=\"margin-right: auto; margin-left: auto; outline: 0px; vertical-align: baseline; display: block; max-width: 550px; background: transparent;\"/></p><p><em class=\"step\" style=\"border: 0px; outline: 0px; font-size: 50px; vertical-align: baseline; color: rgb(255, 50, 50); text-align: center; font-family: arial; height: 50px; width: 100px; display: block; float: left; line-height: 50px; background: transparent;\">4.</em></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-right: 20px; padding-bottom: 10px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; position: relative; background: transparent;\">将所有水果倒入大碗内拌匀。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-right: 20px; padding-bottom: 10px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; position: relative; background: transparent;\"><img class=\"conimg\" src=\"http://site.meishij.net/rs/237/178/1232237/n1232237_142459126228705.jpg\" alt=\"\" style=\"margin-right: auto; margin-left: auto; outline: 0px; vertical-align: baseline; display: block; max-width: 550px; background: transparent;\"/></p><p><em class=\"step\" style=\"border: 0px; outline: 0px; font-size: 50px; vertical-align: baseline; color: rgb(255, 50, 50); text-align: center; font-family: arial; height: 50px; width: 100px; display: block; float: left; line-height: 50px; background: transparent;\">5.</em></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-right: 20px; padding-bottom: 10px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; position: relative; background: transparent;\">取一个盘子，将水果装盘。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-right: 20px; padding-bottom: 10px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; position: relative; background: transparent;\"><img class=\"conimg\" src=\"http://site.meishij.net/rs/237/178/1232237/n1232237_142459130615681.jpg\" alt=\"\" style=\"margin-right: auto; margin-left: auto; outline: 0px; vertical-align: baseline; display: block; max-width: 550px; background: transparent;\"/></p><p><em class=\"step\" style=\"border: 0px; outline: 0px; font-size: 50px; vertical-align: baseline; color: rgb(255, 50, 50); text-align: center; font-family: arial; height: 50px; width: 100px; display: block; float: left; line-height: 50px; background: transparent;\">6.</em></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-right: 20px; padding-bottom: 10px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; position: relative; background: transparent;\">沙拉酱装入裱花袋里。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-right: 20px; padding-bottom: 10px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; position: relative; background: transparent;\"><img class=\"conimg\" src=\"http://site.meishij.net/rs/237/178/1232237/n1232237_142459114292521.jpg\" 